import { useNavigate } from "react-router-dom";
//import axios from "axios";
import { useState } from "react";


const UserSignUp = () => {
 let Navigate = useNavigate();
    const [formData, setFormData] = useState({
        firstName: '',
        lastName: '',
        email: '',
        password: '',
      });
    
      const handleInputChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
      };
    
      const handleSignup = async (e) => {
        e.preventDefault();
        try {
          // Send signup data to the backend
         // const response =  axios.post('http://localhost:5000/api/user/signup', formData);
          const response =  fetch('http://localhost:5000/api/user/signup', {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
          },
            body: JSON.stringify(formData),
          });
    
          // Check if signup was successful
          if (response.ok) {
            // Handle successful signup, e.g., redirect to login page
            console.log('Signup successful!');
            Navigate('/user/login');
          } else {
            // Handle error responses from the backend
            const errorData = await response.json();
            console.error('Signup failed:', errorData.message);
          }
        } catch (error) {
          console.error('Error during signup:', error.message);
        }
      };
    
    




    return (
        <>   
        <body className="form-body">
                <div>
                    <h2 className="admin-singup">User Signup</h2>
                    <p class="error"></p>
                </div>
                <div>
                <div>
                    <button id="try-it-free"><strong>Try it free 7 days</strong> then $20/mo. thereafter</button>
                </div>

                <div className="form-container">
                    <form onSubmit={handleSignup} >
                        <label htmlFor="firstname">
                        </label>
                        <input type="text" placeholder="First Name" value={formData.firstName} onChange={handleInputChange}/>

                        <label htmlFor="lastname">
                        </label>
                        <input type="text" placeholder="Last Name" value={formData.lastName} onChange={handleInputChange}/>

                        <label htmlFor="email">
                        </label>
                        <input type="email" placeholder="Email Address" value={formData.email} onChange={handleInputChange}/>

                        <label htmlFor="password">
                        </label>
                        <input type="password" placeholder="Password" value={formData.password} onChange={handleInputChange}/>
                        <button type="SUBMIT" className="signup">Signup</button> 

                <h6>By clicking the button, you are agreeing to our <a href="#">Terms and Services</a></h6>
            </form>
            </div>
        </div>
        </body>
        </>
    )
}

export default UserSignUp;
