import { useNavigate } from "react-router-dom";
import axios from "axios";
import { useState } from "react";


const UserSignUp = () => {
 let Navigate = useNavigate();
    const [firstName, setFirstName] = useState("");
    const [lastName, setLastName] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [signup, setSignup] = useState(false);
        
    
      const handleSubmit = (e) => {
        e.preventDefault();
        alert("Submited");
      }
      const configuration =  {
        method: 'POST',
        url: 'http://localhost:5000/api/user/signup',
        data: {
          firstName,
          lastName,
          email,
          password
        }
      }
      axios(configuration)
      .then((result) => {
         console.log(result);
         setSignup(true);
         signup ? Navigate('/user/login') : <></>;
        })
      . catch ((error) => {
        console.log('Error during signup');
      }) 
    
 // Navigate('/user/login');
    
    




    return (
        <>   
        <body className="form-body">
       {/* display success message */}
        {signup ? (
          <p className="text-success center">You Are Registered Successfully</p>
        ) : (
          <></>
        )}

                <div>
                    <h2 className="admin-singup center">User Signup</h2>
                    <p class="error"></p>
                </div>
                <div>
                <div>
                    <button id="try-it-free"><strong>Try it free 7 days</strong> then $20/mo. thereafter</button>
                </div>

                <div className="form-container">
                    <form onSubmit={(e)=>handleSubmit(e)}>
                        <label htmlFor="firstname">
                        </label>
                        <input type="text" placeholder="First Name" value={firstName}  onChange={(e) => setFirstName(e.target.value)}/>

                        <label htmlFor="lastname">
                        </label>
                        <input type="text" placeholder="Last Name" value={lastName} onChange={(e) => setLastName(e.target.value)}/>

                        <label htmlFor="email">
                        </label>
                        <input type="email" placeholder="Email Address" value={email} onChange={(e) => setEmail(e.target.value)}/>

                        <label htmlFor="password">
                        </label>
                        <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)}/>
                        <button type="SUBMIT" className="signup" onClick={(e) => {handleSubmit(e)}}>Signup</button> 

                <h6>By clicking the button, you are agreeing to our <a href="#">Terms and Services</a></h6>
            </form>
            </div>
        </div>
        </body>
        </>
    )
}

export default UserSignUp;
