import { useLocation } from "react-router-dom";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { useState } from "react";


const UserLogin = () => {

    let Navigate = useNavigate();
    const [firstName, setFirstName] = useState("");
    const [lastName, setLastName] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [login, setLogin] = useState(false);
        
    
      const handleSubmit = (e) => {
        e.preventDefault();
        alert("Submited");
      }
      const configuration =  {
        method: 'POST',
        url: 'http://localhost:5000/api/user/login',
        data: {
          email,
          password
        }
      }
      
      axios(configuration)
      .then((result) => {
         console.log(result);
         setLogin(true);
        })
      . catch ((error) => {
        console.log('Error during signup');
      }) 
    



    return (
<> 
         <body className="login-form">
         {login ? (
          <p className="text-success">You Are Logged in Successfully</p>
        ) : (
          <p className="text-danger">You Are Not Logged in</p>
        )}

            <div>
                <h2 className="admin-singup">User Login</h2>
                <p class="error"></p>
            </div>
        <div>
		    <div>
		    	<button id="try-it-free"><strong>Try it free 7 days</strong> then $20/mo. thereafter</button>
		    </div>
        
		    <div className="form-container">
		    	<form action="" onSubmit={(e)=>handleSubmit(e)}>
                    <label htmlFor="email">
                    </label>
		    		<input type="email" placeholder="Email Address" value={email} onChange={(e) => setEmail(e.target.value)}/>
                    
                    <label htmlFor="password">
                    </label>
		    		<input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)}/>
		    		<button type="SUBMIT" className="signup" onClick={(e) => {handleSubmit(e)}}>Login</button> 

		    	</form>
		    </div>
	    </div>
        </body>
    </>
    )
}

export default UserLogin;
