import { useState } from "react";
import quizData from "../assets/quizData.json"

const Question = () => {
  //  let total = quizData[0].questions.length;
//    console.log(total)

let [subjectIndex, setSubjectIndex] = useState(0);
let [questionIndex, setQuestionIndex] = useState(0);
let [optionIndex, SetOptionIndex] = useState(0);
let [question, setQuestion] = useState(quizData[subjectIndex].questions[questionIndex].question);
let [option, setOption] = useState(quizData[subjectIndex].questions[questionIndex].options[optionIndex]);

console.log(quizData);
console.log(quizData.length);
console.log(quizData[subjectIndex].questions[questionIndex]);
console.log(quizData[subjectIndex].questions[questionIndex].question);
console.log(quizData[subjectIndex].questions[questionIndex].options);
console.log(quizData[subjectIndex].questions[questionIndex].options[0]);
console.log(quizData[subjectIndex].questions[questionIndex].options[1]);
console.log(quizData[subjectIndex].questions[questionIndex].options[2]);
console.log(quizData[subjectIndex].questions[questionIndex].options[3]);


console.log(quizData);
console.log(quizData.length);
console.log(quizData[subjectIndex].questions[questionIndex+1]);
console.log(quizData[subjectIndex].questions[questionIndex+1].options);
console.log(quizData[subjectIndex].questions[questionIndex+1].options[0]);
console.log(quizData[subjectIndex].questions[questionIndex+1].options[1]);
console.log(quizData[subjectIndex].questions[questionIndex+1].options[2]);
console.log(quizData[subjectIndex].questions[questionIndex+1].options[3]);


const submtAnswer = () => {


    //setSubjectIndex(++subjectIndex)
    setQuestionIndex(++questionIndex);
    setQuestion(quizData[subjectIndex].questions[questionIndex]);
    SetOptionIndex(++optionIndex);
    setOption(quizData.questions[questionIndex].options[optionIndex]);
}
    return (
        <>
        <body className="flex question">
            <div>
                <p className="italic">Question {subjectIndex+1} of 10 </p>
                <h2>quizData[subjectIndex].questions[questionIndex].question
                </h2>
            </div>

            <div>
                <div className="flex start options">
                    <h6>A</h6>
                    <p>{quizData[subjectIndex].questions[questionIndex+1].options[0]}</p>
                </div>
                <div className="flex start options">
                    <h6>B</h6>
                    <p>{quizData[subjectIndex].questions[questionIndex+1].options[1]}</p>
                </div>
                <div className="flex start options">
                    <h6>A</h6>
                    <p>{quizData[subjectIndex].questions[questionIndex+1].options[2]}</p>
                </div>
                <div className="flex start options">
                   <h6>A</h6>
                    <p>{quizData[subjectIndex].questions[questionIndex+1].options[3]}</p>
                </div>
                <button onClick={submtAnswer}>Submit Answer</button>
            </div>
        </body>
        </>
    )
}

export default Question;
