import { useEffect, useState } from "react";
import quizData from "../assets/quizData.json";
import { useLocation, useNavigate } from "react-router-dom";
import { Link } from "react-router-dom";

const Question = () => {
let [data, setData] = useState(null);
const location = useLocation();
let [subjectIndex, setSubjectIndex] = useState(0);
let [questionIndex, setQuestionIndex] = useState(0);
let [optionIndex, setOptionIndex] = useState(0);
let [option, setOption] = useState(quizData[subjectIndex].questions[questionIndex].options[optionIndex]);
let [question, setQuestion] = useState(null);
const [token, setToken] = useState(localStorage.getItem('token'))
const Navigate = useNavigate(); 
const handleLogout = () => {
    // Clearing the token from local storage
    localStorage.removeItem('token');
    Navigate("/user/login")
    // Clearing the token from state
    setToken(null);
  };

  
useEffect(() => {
    fetch("http://localhost:5000/api/quizData")
    .then (res => {
        return res.json();
    })
    .then(dataQuiz => {
        console.log(dataQuiz);
        setData(dataQuiz);
        setQuestion(dataQuiz[subjectIndex].questions[questionIndex].question);
    })
    .catch((err) => {
    console.log(err);
    })


    const {state} = location;
   
    {state && state.suitableQuestion 
    ? (setSubjectIndex(subjectIndex)) : state && state.suitableQuestion2
    ? (setSubjectIndex(subjectIndex+1)): state && state.suitableQuestion3
    ? (setSubjectIndex(subjectIndex+2)):state && state.suitableQuestion4
    ? (setSubjectIndex(subjectIndex+3)):
    (<></>)
};
    
}, [])

const submtAnswer = () => {
    setQuestionIndex(++questionIndex);
    setQuestion(data[subjectIndex].questions[questionIndex].question);

    
    {questionIndex === data[subjectIndex].questions.length - 1 ? (Navigate('/result')) : (<></>)};
}

const optio = (u) => {
    console.log(u.target.lastChild.innerHTML);

}
    return (
        <>
       {token ? ( 
     <      body>
      {token ? (
        <div className="flex welcome-div">
          <Link to={"/subjects"} style={{ textDecoration: "none", color: "white" }} className="header-login left"><i class="fa fa-angle-double-left" aria-hidden="true"></i>
            Back</Link>
          <Link style={{ textDecoration: "none", color: "white" }} className="header-login right" onClick={handleLogout}>Logout</Link>
        </div>
      ) : (
        Navigate("/user/login")
      )}
      <div className="flex question">
        
      <div>
          <p className="italic">Question {questionIndex+1} of {quizData[subjectIndex].questions.length} </p>
          <h2>{question}
          </h2> 

      </div>

      <div>
          <div className="flex start options" onClick={optio}>
              <h6>A</h6>
              <p>{quizData[subjectIndex].questions[questionIndex].options[0]}</p>
          </div>
          <div className="flex start options" onClick={optio}>
              <h6>B</h6>
              <p>{quizData[subjectIndex].questions[questionIndex].options[optionIndex+1]}</p>
          </div>
          <div className="flex start options" onClick={optio}>
              <h6>C</h6>
              <p>{quizData[subjectIndex].questions[questionIndex].options[optionIndex+2]}</p>
          </div>
          <div className="flex start options" onClick={optio}>
             <h6>D</h6>
              <p>{quizData[subjectIndex].questions[questionIndex].options[optionIndex+3]}</p>
          </div>
          <button onClick={submtAnswer}>Submit Answer</button>
      </div>
      </div>
    </body>
            ) : (
        <h3 className="error">You have to be logged in before you can view this page, click <Link to={"/user/login"} style={{color: "white" }}> here </Link> to log in </h3>
            )}
        </>
    )
}

export default Question;
