import { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const AdminLogin = () => {
    const Navigate = useNavigate();
    const [details, setDetails] = useState({
        email: '',
        password: '',
      });
    
      const handleChange = (e) => {
        setDetails({
          ...details,
          [e.target.name]: e.target.value,
        });
      };
    
      const handleLogin = async () => {
        try {
          const response = await fetch('http://localhost:5000/user/login', {
            method: 'POST',
           // headers: {
           ////   'Content-Type': 'application/json',
           //// },
           // body: JSON.stringify(credentials),
          });
    
          if (!response.ok) {
            const data = await response.json();
            throw new Error(data.message);
          }
    
          console.log('Login successful');
        } catch (error) {
          console.error('Login Error:', error.message);
        }
      };
    
    

    return (
   <>
          <body className="login-form">
            <div>
                <h2 className="admin-singup">Admin Login</h2>
                <p class="error"></p>
            </div>
        <div>
		    <div>
		    	<button id="try-it-free"><strong>Try it free 7 days</strong> then $20/mo. thereafter</button>
		    </div>
        
		    <div className="form-container">
		    	<form action="" method="GET">
                    <label htmlFor="email">
                    </label>
		    		<input type="email" name="email" value={details.email}  placeholder="Email Address"/>
                    
                    <label htmlFor="password">
                    </label>
		    		<input name="password" value={details.password} type="password" placeholder="Password"/>
		    		<button type="SUBMIT" className="signup">Login</button> 

		    	</form>
		    </div>
	    </div>
        </body>
   </>
    )
}

export default AdminLogin;
