import { Link } from "react-router-dom";
import quizData from "../assets/quizData.json"
import { useNavigate } from "react-router-dom";
import { useState, useEffect } from "react";


const Subjects = () => {
    let [subjectIndex, setSubjectIndex] = useState(0);
    let [question, setQuestion] = useState(quizData[subjectIndex]);

    let mathematics = quizData[subjectIndex].subject;
    let UOE = quizData[subjectIndex+1].subject;
    let PHY = quizData[subjectIndex+2].subject;
    let CHM = quizData[subjectIndex+3].subject;
    console.log(mathematics)
    console.log(UOE);
    console.log(PHY);
 //data = quizData.find();
// console.log(data); 

useEffect(() => {

    fetch("http://localhost:5000/api/quizData")
    .then((res => {
        res.json()})
        .then ((data) => {
            quizData(data)
        }))
        .catch(error => {
            console.log({message: "error", error})
        })
    }, [])
        //setSubjectIndex(++subjectIndex);
        //setQuestion(subjectIndex)
        //console.log(setQuestion);
    let Navigate = useNavigate();
    const testing = (e) => {
        console.log(e.target.id);
        console.log(e.target.lastChild.innerHTML);

       // e.target.innerHTML !== subject ? (console.log("condition is met and i don't know what's wrong")) : (console.log("condition is not met"));
        e.target.id === mathematics 
        ? (Navigate('/question', { state: { suitableQuestion: true }})): e.target.id === UOE
        ? (Navigate('/question', { state: { suitableQuestion2: true }})): e.target.id === PHY
        ? (Navigate('/question', { state: { suitableQuestion3: true }})): e.target.id === CHM
        ? (Navigate('/question', { state: { suitableQuestion4: true }}))
        : (console.log("condition is not met"));
//        navigate('/new-route', { state: { changeBackgroundColor: true } });
    }
    return (
        <>
            <body className="flex start-align">
                <div>
                    <h1>Welcome to <span>My Quiz!</span></h1>
                    <p className="italic">Pick a subject to get started</p>
                </div>
                <div>
                    <div className="flex start options2" id="Use of English" onClick={testing}>
                        <h6>UOE</h6>
                        <h3 onClick={testing}>Use of English</h3>
                    </div>
                    <div className="flex start options2" id="Mathematics" onClick={testing}>
                        <h6>MTH</h6>
                        <h3>Mathematics</h3>
                    </div>
                    <div className="flex start options2" id="Physics" onClick={testing}>
                        <h6>PHY</h6>
                       <h3>Physics</h3>
                     </div>
                    <div className="flex start options2" id="Chemistry" onClick={testing}>
                        <h6>CHM</h6>
                        <h3>Chemistry</h3>
                    </div>
                </div>
            </body>
        </>
    )
}

export default Subjects;
