import { useState } from "react";
import quizData from "../assets/quizData.json"

const Question = () => {

let [subjectIndex, setSubjectIndex] = useState(0);
let [questionIndex, setQuestionIndex] = useState(0);
let [optionIndex, SetOptionIndex] = useState(0);
let [question, setQuestion] = useState(quizData[subjectIndex].questions[questionIndex].question);
let [option, setOption] = useState(quizData[subjectIndex].questions[questionIndex].options[optionIndex]);



const submtAnswer = () => {
    //setSubjectIndex(++subjectIndex)
    setQuestionIndex(++questionIndex);
    setQuestion(quizData[subjectIndex].questions[questionIndex]);
    SetOptionIndex(++optionIndex);
    setOption(quizData.questions[questionIndex].options[optionIndex]);
}
let total = quizData[0].questions.length;
console.log(total)
    return (
        <>
        <body className="flex question">
            <div>
                <p className="italic">Question {subjectIndex+1} of {total} </p>
                <h2>{question[subjectIndex].questions.question}
                </h2>
            </div>

            <div>
                <div className="flex start options">
                    <h6>A</h6>
                    <p>{question[subjectIndex].questions[questionIndex].options[optionIndex]}</p>
                </div>
                <div className="flex start options">
                    <h6>B</h6>
                    <p>{question[subjectIndex].questions[questionIndex].options[optionIndex+1]}</p>
                </div>
                <div className="flex start options">
                    <h6>A</h6>
                    <p>{question[subjectIndex].questions[questionIndex].options[optionIndex+2]}</p>
                </div>
                <div className="flex start options">
                   <h6>A</h6>
                    <p>{question[subjectIndex].questions[questionIndex].options[optionIndex+3]}</p>
                </div>
                <button onClick={submtAnswer}>Submit Answer</button>
            </div>
        </body>
        </>
    )
}

export default Question;
