import { Link } from "react-router-dom";
import quizData from "../assets/quizData.json"
import { useNavigate } from "react-router-dom";
import { useState } from "react";


const Subjects = () => {
    let [subjectIndex, setSubjectIndex] = useState(0);
    let [question, setQuestion] = useState(quizData[subjectIndex]);

    let subject = quizData[subjectIndex].subject;
    console.log(subject)
 data = quizData.find();
// console.log(data); 

        //setSubjectIndex(++subjectIndex);
        //setQuestion(subjectIndex)
        //console.log(setQuestion);
    let Navigate = useNavigate();
    const testing = (e) => {
        console.log(e.target.lastChild.innerHTML);

       // e.target.innerHTML !== subject ? (console.log("condition is met and i don't know what's wrong")) : (console.log("condition is not met"));
        e.target.lastChild.innerHTML === subject 
        ? (Navigate('/question', { state: { suitableQuestion: true }}))
        : (console.log("condition is not met"));
//        navigate('/new-route', { state: { changeBackgroundColor: true } });
    }
    return (
        <>
            <body className="flex start-align">
                <div>
                    <h1>Welcome to <span>My Quiz!</span></h1>
                    <p className="italic">Pick a subject to get started</p>
                </div>
                <div>
                    <div className="flex start options2" onClick={testing}>
                        <h6>UOE</h6>
                        <h3>Use of English</h3>
                    </div>
                    <div className="flex start options2">
                        <h6>MTH</h6>
                        <h3><Link to={"/question"} style={{ textDecoration: "none", color: "white" }}>Mathematics</Link></h3>
                    </div>
                    <div className="flex start options2">
                        <h6>PHY</h6>
                       <h3><Link to={"/question"} style={{ textDecoration: "none", color: "white" }}>Physics</Link></h3>
                     </div>
                    <div className="flex start options2">
                        <h6>CHM</h6>
                        <h3><Link to={"/question"} style={{ textDecoration: "none", color: "white" }}>Chemistry</Link></h3>
                    </div>
                </div>
            </body>
        </>
    )
}

export default Subjects;
