import { useEffect, useState } from "react";
//import quizData from "../assets/quizData.json";
import { useLocation, useNavigate } from "react-router-dom";
const Question = () => {
  //  let total = quizData[0].questions.length;
//    console.log(total)

let [data, setData] = useState(null);
const location = useLocation();
let [subjectIndex, setSubjectIndex] = useState(0);
let [questionIndex, setQuestionIndex] = useState(0);
let [optionIndex, SetOptionIndex] = useState(0);
let Navigate = useNavigate();


fetch("http://localhost:5000/api/quizData")
.then (res => {
    return res.json();
})
.then(quizData => {
    console.log(quizData);
    setData(quizData);
    {data === 10 ? ('result') : (<></>)}
})
.catch((err) => {
console.log(err);
})

  
useEffect(() => { 
    //let [suitableQuestion, setSuitableQuestion] = useState(quizData[subjectIndex]);
    const {state} = location;
//    console.log(state);
//    console.log(state.suitableQuestion);
   {state && state.suitableQuestion 
    ? (setSubjectIndex(subjectIndex)) : state && state.suitableQuestion2
    ? (setSubjectIndex(subjectIndex+1)): state && state.suitableQuestion3
    ? (setSubjectIndex(subjectIndex+2)):state && state.suitableQuestion4
    ? (setSubjectIndex(subjectIndex+3)):
    (<></>)
};
    
}, [])

let [question, setQuestion] = useState(setData[subjectIndex].questions[questionIndex]);
let [option, setOption] = useState(setData[subjectIndex].questions[questionIndex].options[optionIndex]);
//let [submtAnswer, setSubmtAnswer] = useState(true);

const submtAnswer = () => {
    setQuestionIndex(++questionIndex);
    setQuestion(setData[subjectIndex].questions[questionIndex]);
    
    
    //{u.target.lastChild.innerHTML = null ? (<></>) : (submtAnswer)};
    {questionIndex === 10 ? (Navigate('/result')) : (<></>)};
}

const optio = (u) => {
    console.log(u.target.lastChild.innerHTML);

}

    return (
        <>
        
      <body className="flex question">
      <div>
          <p className="italic">Question {questionIndex+1} of {setData[subjectIndex].questions.length} </p>
          <h2>{setData[subjectIndex].questions[questionIndex].question}
          </h2>
      </div>

      <div>
          <div className="flex start options" onClick={optio}>
              <h6>A</h6>
              <p>{setData[subjectIndex].questions[questionIndex].options[0]}</p>
          </div>
          <div className="flex start options" onClick={optio}>
              <h6>B</h6>
              <p>{setData[subjectIndex].questions[questionIndex].options[1]}</p>
          </div>
          <div className="flex start options" onClick={optio}>
              <h6>C</h6>
              <p>{setData[subjectIndex].questions[questionIndex].options[2]}</p>
          </div>
          <div className="flex start options" onClick={optio}>
             <h6>D</h6>
              <p>{setData[subjectIndex].questions[questionIndex].options[3]}</p>
          </div>
          <button onClick={submtAnswer}>Submit Answer</button>
      </div>
  </body>
        </>
    )
}

export default Question;
